<?php

namespace Labs\AdminBundle\Repository;

use Labs\AdminBundle\Entity\Post;
use Labs\AdminBundle\Entity\Users;

/**
 * PostRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param Users $user
     * @return mixed
     * @throws \Doctrine\ORM\NonUniqueResultException
     */
    public function getDraftUser(Users $user)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->where(
            $qb->expr()->eq('p.user', ':user'),
            $qb->expr()->eq('p.draft', 0)
        );
        $qb->setParameter('user', $user);
        return $qb->getQuery()->getOneOrNullResult();
    }

    /**
     * @return array
     */
    public function getAll()
    {
        $qb = $this->createQueryBuilder('p');
        $qb->where('p.draft <> 0');
        $qb->orderBy('p.created', 'Desc');
        return $qb->getQuery()->getResult();
    }

    /**
     * @param Users $user
     * @param Post $post
     * @return mixed
     * @throws \Doctrine\ORM\NonUniqueResultException
     * Recupere l'article de l'utilisateur couramment connecté avec l'id passé en paramètre
     */
    public function getPostForUser(Users $user, Post $post)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.user', 'u');
        $qb->addSelect('u');
        $qb->where(
            $qb->expr()->eq('p.id', ':post'),
            $qb->expr()->eq('u.id', ':user')
        );
        $qb->setParameter('post', $post);
        $qb->setParameter('user', $user);
        return $qb->getQuery()->getOneOrNullResult();
    }

    /**
     * @param Post $post
     * @return mixed
     * @throws \Doctrine\ORM\NonUniqueResultException
     * Recupere l'article avec l'id passé en paramètre
     */
    public function getArticles(Post $post)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->where(
            $qb->expr()->eq('p.id', ':post')
        );
        $qb->setParameter('post', $post);
        return $qb->getQuery()->getOneOrNullResult();
    }

    public function getPostSlug(Post $post, $slug)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.medias','m');
        $qb->addSelect('m');
        $qb->leftJoin('p.item','i');
        $qb->addSelect('i');
        $qb->leftJoin('i.section','s');
        $qb->addSelect('s');
        $qb->where(
            $qb->expr()->eq('p.id', ':post'),
            $qb->expr()->eq('p.slug', ':slug'),
            $qb->expr()->eq('m.actived', 1)
        );
        $qb->setParameter('post', $post);
        $qb->setParameter('slug', $slug);
        return $qb->getQuery()->getOneOrNullResult();
    }

    /**
     * @param $min
     * @param null $max
     * @return array
     */
    public function OldPost($min ,$max)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.medias','m');
        $qb->addSelect('m');
        $qb->where(
            $qb->expr()->neq('p.id', ':min')
        );
        $qb->orderBy('p.created', 'DESC');
        $qb->setMaxResults($max);
        $qb->setParameter('min', $min);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param $max
     * @return array
     * Recupère tout les articles avec un param $max
     */
    public function findArticleNum($max)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.medias', 'm');
        $qb->addSelect('m');
        $qb->leftJoin('p.item', 'i');
        $qb->addSelect('i');
        $qb->leftJoin('i.section', 's');
        $qb->addSelect('s');
        $qb->where($qb->expr()->eq('p.online', 1));
        $qb->andWhere($qb->expr()->eq('m.actived', 1));
        $qb->orderBy('p.created', 'Desc');
        $qb->setMaxResults($max);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param array $options
     * @param int $max
     * @return array
     * Recupère les derniers articles par date de création et en fonction d'un param $max des
     * Sous rubrique associés en par Rubrique courante
     */
    public function findPostItemBySection($options = array(), $max = 9)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.medias', 'm');
        $qb->addSelect('m');
        $qb->leftJoin('p.item', 'i');
        $qb->addSelect('i');
        $qb->leftJoin('i.section', 's');
        $qb->addSelect('s');
        $qb->where(
            $qb->expr()->eq('p.online', 1),
            $qb->expr()->eq('m.actived', 1),
            $qb->expr()->eq('s.id', ':options')
        );
        $qb->orderBy('p.created', 'Desc');
        $qb->setMaxResults($max);
        $qb->setParameter('options', $options);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param $options
     * @param int $max
     * @return array
     * Recupère Un Articles par Items
     */
    public function getPostByItems($options, $max = 7)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.item', 'i');
        $qb->addSelect('i');
        $qb->leftJoin('p.medias', 'm');
        $qb->addSelect('m');
        $qb->where(
            $qb->expr()->eq('p.online', 1),
            $qb->expr()->eq('m.actived', 1),
            $qb->expr()->eq('i.section', ':options')
        );
        $qb->orderBy('p.created', 'Desc');
        $qb->setMaxResults($max);
        $qb->setParameter('options', $options);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param $options
     * @param int $max
     * @return array
     */
    public function getCountPostByItems($options, $max = null)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->leftJoin('p.item', 'i');
        $qb->addSelect('i');
        $qb->leftJoin('p.medias', 'm');
        $qb->addSelect('m');
        $qb->where(
            $qb->expr()->eq('p.online', 1),
            $qb->expr()->eq('m.actived', 1),
            $qb->expr()->eq('p.item', ':options')
        );
        $qb->orderBy('p.created', 'Desc');
        if($max){
            $qb->setMaxResults($max);
        }
        $qb->setParameter('options', $options);
        return $qb->getQuery()->getResult();
    }
    
    
    
    
}
