$( document ).ready(function() {

    'use strict';

    $("#carousal").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:true,
        dots:false,
        nav:true,
        margin:30,
        items:2,
        singleItem:true,
        responsiveClass:true,
        responsive:{
            0:{
                items:1,
                nav:true
            },
            600:{
                items:2,
                nav:false
            },
            900:{
                items:2,
                nav:true,
                loop:false
            }
        }
    });

    $("#carousal-team").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:true,
        dots:false,
        nav:false,
        margin:10,
        items:3,
        singleItem:true
    });

    $("#carousal3").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:true,
        dots:false,
        nav:true,
        margin:0,
        items:1,
        singleItem:true
    });

    $("#carousal-widget").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:false,
        dots:true,
        nav:false,
        margin:0,
        items:1,
        singleItem:true
    });

    $("#carousal-footer-widget").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:false,
        dots:true,
        nav:false,
        margin:0,
        items:1,
        singleItem:true
    });

    $(".wide-carousal-post").owlCarousel({
        autoplay:false,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:true,
        dots:false,
        nav:true,
        margin:0,
        items:1,
        singleItem:true
    });

    $("#carousal-posts").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:true,
        dots:false,
        nav:true,
        margin:4,
        items:3,
        singleItem:true,
        responsiveClass:true,
        responsive:{
            0:{
                items:1,
                nav:true
            },
            600:{
                items:2,
                nav:false
            },
            900:{
                items:2,
                nav:true,
                loop:false
            },
            1280:{
                items:3,
                nav:true,
                loop:false
            }

        }
    });

    $(".big-post-carousel").owlCarousel({
        autoplay:true,
        autoplayTimeout:3000,
        smartSpeed:2000,
        loop:false,
        dots:true,
        nav:false,
        margin:0,
        items:1,
        singleItem:true
    });

    $(function(){

        /* initiciate jPages */
        $("div.holder").jPages({
            containerID : "itemContainer",
            animation   : "fadeInUp",
            perPage : 4
        });

        /* on select change */
        $("select").change(function(){
            /* get new css animation */
            var newAnimation = $(this).val();

            /* destroy jPages and initiate plugin again */
            $("div.holder").jPages("destroy").jPages({
                containerID   : "itemContainer",
                animation     : newAnimation
            });
        });

    });

});
